package com.company.proxy;

public class ProxyImage implements Image {

    private String name;
    private RealImage realImage;

    public ProxyImage(String name) {
        this.name = name;
        this.realImage = null;  // Real image is not loaded during initialization, so creation of image is fast.
    }

    @Override
    public void displayImage() {
        // Load the real image when image is displayed for the first time.
        if (this.realImage == null)
            this.realImage = new RealImage(this.name);

        this.realImage.displayImage();
    }
}

java_files.proxy/Main.java:
package com.company.proxy;

public class Main {

    public static void main(String[] args) {

        System.out.println("Create all images (it is fast, because nothing is loaded).");

        // Create first image
        Image image1 = new ProxyImage("image1");

        // Create second image
        Image image2 = new ProxyImage("image2");

        System.out.println("Display all images twice (images are loaded before the first display).");

        // The images are loaded and displayed twice
        image1.displayImage();
        image2.displayImage();
        image1.displayImage();
        image2.displayImage();
    }
}

