package VisitorCalculateBasket;

public interface Element
{
    void accept(Visitor visitor);
}

package VisitorCalculateBasket;

import java.util.ArrayList;
import java.util.List;

public class Basket
{
    private List<Element> elements = new ArrayList<>();

    public Basket()
    {
        this.elements = elements;
    }

    public void buyArticle(Article article)
    {
        elements.add(article);
    }

    public void applyVoucher(Voucher voucher)
    {
        elements.add(voucher);
    }

    public List<Element> getElements()
    {
        return this.elements;
    }
}

package VisitorCalculateBasket;

public interface Visitor
{
    void visit(Article article);
    void visit(Voucher voucher);
}

package VisitorCalculateBasket;

public class Article implements Element
{
    private String description;
    private double price;

    public Article(String description, double price)
    {
        this.description = description;
        this.price = price;
    }

    public String getDescription()
    {
        return this.description;
    }

    public double getPrice()
    {
        return this.price;
    }

    @Override
    public void accept(Visitor visitor)
    {
        visitor.visit(this);
    }
}

package VisitorCalculateBasket;

public class CalculateTotal implements Visitor
{
    private static double total = 0;

    @Override
    public void visit(Article article)
    {
        total += article.getPrice();
    }

    @Override
    public void visit(Voucher voucher)
    {
        total -= voucher.getPrice();
    }

    public double getTotal()
    {
        return this.total;
    }
}

package VisitorCalculateBasket;

public class PrintReceipt implements Visitor
{
    @Override
    public void visit(Article article)
    {
        System.out.println("Article visited with receipt: " + article.getDescription());
    }

    @Override
    public void visit(Voucher voucher)
    {
        System.out.println("Voucher visited with receipt: " + voucher.getDescription());
    }
}

package VisitorCalculateBasket;

import java.util.List;

public class Main
{
    public static void main(String[] args)
    {
        Article toy = new Article("basic toy", 5);
        Article doll = new Article("doll", 4);
        double totalPriceInitial = toy.getPrice() + doll.getPrice();
        System.out.println("initial price of items: " + totalPriceInitial);

        Voucher kupon = new Voucher("zlava", 3);

        Basket basket = new Basket();
        basket.buyArticle(toy);
        basket.buyArticle(doll);
        basket.applyVoucher(kupon);

        CalculateTotal totalPrice = new CalculateTotal();
        PrintReceipt receiptPrinter = new PrintReceipt();

        List<Element> basketElements = basket.getElements();

        for (Element element : basketElements)
        {
            if (element instanceof Article)
            {
                totalPrice.visit((Article)element);
                receiptPrinter.visit((Article)element);
            }
            else if (element instanceof Voucher)
            {
                totalPrice.visit((Voucher)element);
                receiptPrinter.visit((Voucher)element);
            }
        }

        System.out.println("Total price after applying voucher: " + totalPrice.getTotal());
    }
}

package VisitorCalculateBasket;

public class Voucher implements Element
{
    private String description;
    private double price;

    public Voucher(String description, double price)
    {
        this.description = description;
        this.price = price;
    }

    public String getDescription()
    {
        return this.description;
    }

    public double getPrice()
    {
        return this.price;
    }

    @Override
    public void accept(Visitor visitor)
    {
        visitor.visit(this);
    }
}

