/**
 *
 * @author viktorooo
 */
public class Adapter extends AdapteeList implements TargetStack{

    public Adapter(){}

    @Override
    public void push(String o) {
        insertTail(o);

    }

    @Override
    public void pop() {
        removeTail();
    }

}

