        //pretypovanie ??? 
        System.out.println(iterator.first().getMeno()+iterator.first().getPriezvisko()+iterator.first().getPlat());
        System.out.println(iterator.next().getMeno()+iterator.currentItem().getPriezvisko()+iterator.currentItem().getPlat());
