 */
public class Jablko implements Prototype {
    private String meno;

    public void setName(String nam) {
        this.meno = nam;
    }

    public String getName() {
        return this.meno;
    }

    @Override
    public Prototype makeCopy() {
        try {
            return (Prototype) this.clone();
        } catch (CloneNotSupportedException ex) {
