/**
 *
 * @author Stevo
 */
public class ProxyMachine implements FBIwebPage {

    private FBIwebPage tempPage;

    public ProxyMachine(FBIwebPage newFBIwebPage) {
        tempPage = newFBIwebPage;
    }

    @Override
    public void request(String msg) {
        switch(msg){
            case "hacker": 
            case "megaupload": 
            case "anonymous": 
                tempPage.request("0"+msg+"0");
                break;
