public class PersonItarator implements Iterator{

    private int current = 0;

    @Override
    public boolean hasNext() {
        if(PersonCollection.persons.size() == current)
        {
            return false;
        }
        else
        {
            return true;
        }
    }

    @Override
    public Person getNext() {
        return PersonCollection.persons.get(current++);
    }

    @Override
    public Person First() {
